package nl.degrijs.router;

public class RouterException extends Error {

	private static final long serialVersionUID = -7357283402502818832L;

	public RouterException() {
		super();
		// TODO Auto-generated constructor stub
	}

	public RouterException(String message, Throwable cause, boolean enableSuppression, boolean writableStackTrace) {
		super(message, cause, enableSuppression, writableStackTrace);
	}

	public RouterException(String message, Throwable cause) {
		super(message, cause);
	}

	public RouterException(String message) {
		super(message);
	}

	public RouterException(Throwable cause) {
		super(cause);
	}

}
